import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, Button, FlatList, Text, View } from 'react-native';
import { CheckBox, Input } from '@rneui/themed';
import { useCallback, useEffect, useState } from 'react';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import AsyncStorage from '@react-native-async-storage/async-storage';

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font));
}

function LoginScreen({ navigation, route }) {
  cacheFonts([FontAwesome.font]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // used Chat GPT to help with AsyncStorage/handleLogin
  const handleLogin = async () => {
    try {
      const loginData = await AsyncStorage.getItem('loginData');
      if (loginData) {
        const users = JSON.parse(loginData);
        const user = users.find(u => u.username === username && u.password === password);
        const userTest = users.find(() => username === 'test' && password === 'Test1@');
        if (user || userTest) {
          console.log(loginData);
          navigation.navigate('Todo');
        } else {
          alert('Invalid username or password');
        }
      } else {
        alert('No users registered yet');
      }
    } catch (error) {
      console.error('Error retrieving login data: ', error);
    }
  };

  return (
    <SafeAreaView style={[styles.container]}>
      <Input placeholder='Username' testID='login-username' value={username} onChangeText={(value) => { setUsername(value) }} />
      <Input placeholder='Password' testID='login-password' value={password} secureTextEntry={true} onChangeText={(value) => { setPassword(value) }} />
      <Button title='Login' testID='login-button' onPress={handleLogin} />
      <Button title='Register' testID='login-register' onPress={() =>
        navigation.navigate('Registration')} />
    </SafeAreaView>
  )
}

function RegistrationScreen({ navigation, route }) {
  cacheFonts([FontAwesome.font]);
  const [firstname, setFirstName] = useState('');
  const [lastname, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [phonenumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [zip, setZip] = useState('');

  // used Chat GPT to help with AsyncStorage/handleRegister
  const handleRegister = async () => {
    try {
      const loginData = await AsyncStorage.getItem('loginData');
      const users = loginData ? JSON.parse(loginData) : [];
      users.push({ username, password });
      await AsyncStorage.setItem('loginData', JSON.stringify(users));
      if (loginData) {
        alert('User registered successfully');
        console.log(loginData);
        navigation.navigate('Todo');
      }
      else {

      }
    } catch (error) {
      console.error('Error registering user: ', error);
    }
  };
  const [isSelected, setSelection] = useState(false);

  return (
    <SafeAreaView style={[styles.container]}>
      <Input placeholder='First Name' testID='firstname' value='' onChangeText={(value) => { setFirstName(value) }} />
      <Input placeholder='Last Name' testID='lastname' onChangeText={(value) => { setLastName(value) }} />
      <Input placeholder='Username' testID='username' onChangeText={(value) => { setUsername(value) }} />
      <Input placeholder='Phone Number' testID='phonenumber' onChangeText={(value) => { setPhoneNumber(value) }} />
      <Input placeholder='Password' testID='password' secureTextEntry={true} onChangeText={(value) => { setPassword(value) }} />
      <Input placeholder='Confirm Password' testID='confirmpassword' secureTextEntry={true} onChangeText={(value) => { setConfirmPassword(value) }} />
      <Input placeholder='Email' testID='email' onChangeText={(value) => { setEmail(value) }} />
      <Input placeholder='ZIP Code' testID='zip' onChangeText={(value) => { setZip(value) }} />
      <CheckBox title={'Sign up for newsletter'} testID='newsletter' value={isSelected} onValueChange={setSelection} />
      <Button title='Submit' onPress={handleRegister} />
    </SafeAreaView>
  )
}

let startTasks = [
  { description: 'Task 1', completed: true, key: 1 },
  { description: 'Task 2', completed: true, key: 2 }
]

function TodoScreen({ navigation }) {
  cacheFonts([FontAwesome.font]);
  let [tasks, setTasks] = useState(startTasks);
  let [input, setInput] = useState('');
  let updateTask = (task) => {
    task.completed = !task.completed;
    setTasks([...tasks]);
  }
  let addTask = () => {
    let maxKey = 0;
    tasks.forEach(task => {
      if (task.key > maxKey) {
        maxKey = task.key;
      }
    });
    setTasks([...tasks, {
      description: input,
      completed: false,
      key: maxKey + 1
    }]);
    console.log(tasks);
    setInput('');
  }
  let renderItem = ({ item }) => {
    return <CheckBox
      textStyle={item.completed ? {
        textDecorationLine: 'line-through', textDecorationStyle: 'solid'
      } : undefined}
      title={item.description}
      checked={item.completed}
      onPress={() => updateTask(item)} />
  }
  return (
    <SafeAreaView style={[styles.container]}>
      <StatusBar style='auto' />
      <FlatList data={tasks} renderItem={renderItem} />
      <Input
        onChangeText={setInput}
        value={input}
        placeholder='New task...'
      ></Input><Button title='Add' onPress={addTask} />
    </SafeAreaView>
  );
}

const Stack = createNativeStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Login'>
        <Stack.Screen name='Login' component={LoginScreen} />
        <Stack.Screen name='Registration' component={RegistrationScreen} />
        <Stack.Screen name='Todo' component={TodoScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}



