import { FlatList, StyleSheet, View } from 'react-native'
import { useState, useEffect } from 'react'
import * as React from 'react'
import { StatusBar } from 'expo-status-bar'
import { NavigationContainer } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { Button, ButtonGroup, Input, Text } from '@rneui/themed'
import * as Font from 'expo-font'
import FontAwesome from '@expo/vector-icons/FontAwesome'


async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  text: {
    fontWeight: 'bold',
    fontSize: 30,
    paddingBottom: 15,
    paddingTop: 15,
  },
  buttonContainer: {
    marginVertical: 10,
    width: 200,
  },
  button: {
    padding: 10,
    marginVertical: 5,
    backgroundColor: '#55D6BE',
    alignItems: 'center',
    borderRadius: 16,
  },
  buttonText: {
    color: '#fff',
    fontWeight: '600',
  },
})


const Stack = createNativeStackNavigator()

const questions = [
  {
    "prompt": "Which shark has been found in the Mississippi River as far inland as Illinois?",
    "type": "multiple-choice",
    "choices": [
      "Tiger Shark",
      "Bull Shark",
      "Reef Shark",
      "Nurse Shark",
    ],
    "correct": 0
  },
  {
    "prompt": "Which are in the top 5 biggest shark species?",
    "type": "multiple-answer",
    "choices": [
      "Whale Shark",
      "Spiny Dogfish",
      "Horn Shark",
      "Basking Shark",
    ],
    "correct": [0, 2]
  },
  {
    "prompt": "Blue sharks are actually blue.",
    "type": "true-false",
    "choices": [
      "True",
      "False",
    ],
    "correct": 1
  },
]


export default function App() {
  //   return (
  //     <NavigationContainer>
  //       <Stack.Navigator initialRouteName=''>
  //         <Stack.Screen name='' component={} />
  //         <Stack.Screen name='' component={} />
  //         <Stack.Screen name='' component={} />
  //       </Stack.Navigator>
  //     </NavigationContainer>
  //   )
  return (
    <View>
      <Question></Question>
    </View>
  )
}


export function Question({ route, navigation }) {
  cacheFonts([FontAwesome.font])
  let [prompt, choices] = route.params

  return (
    <View>
      <Text>{prompt}</Text>
      <ButtonGroup
        buttons={[{choices}]}
        selectMultiple
        selectedIndexes={selectedIndexes}
        onPress={(value) => {
          setSelectedIndexes(value);
        }}
      />
      <Button
      testID='next-question'
      onPress={() => navigation.navigate('', {})}
      >Next</Button>
    </View>
  )
}


export function Summary({ route, navigation }) {
  cacheFonts([FontAwesome.font])

  return (
    <View>
      <Text>Summary</Text>
      <Text testID='total'>Score: {}/3</Text>
    </View>
  )
}
