import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, Button, FlatList, TextInput } from 'react-native';
import { CheckBox, Text } from '@rneui/themed';
import { useState } from 'react';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import React from 'react';

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font));
}

let tasks = [
  {key: '1', description: 'Task 1', completed: true},
  {key: '2', description: 'Task 2', completed: true}
]

export default function App() {
  cacheFonts([FontAwesome.font]);
  let [checked, setChecked] = useState(true);
  let [tasks, setTasks] = useState(tasks);
  let updateTask = (task) => {
    task.completed = !task.completed;
    setTasks([...tasks]);
  }
  let renderItem = ({ item }) => {
    return <CheckBox title={item.description} checked={item.completed} onPress={() => updateTask(item)} />
  }

  return (
    <SafeAreaView style={[styles.container, styles.horizontal]}>
      <StatusBar style='auto' />
      <Text>Tasks</Text>
      <FlatList data={tasks} renderItem={renderItem} />
    </SafeAreaView>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

