import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, Button, FlatList, Text, View } from 'react-native';
import { CheckBox, Input } from '@rneui/themed';
import { useState } from 'react';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native'
import AsyncStorage from '@react-native-async-storage/async-storage';


async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font));
}

let startTasks = [
  {description: 'Task 1', completed: true, key: 1, relatedTasks: [2]},
  {description: 'Task 2', completed: true, key: 2}
]

function createNativeStackNavigator() {

}

const Stack = createNativeStackNavigator();

function TodoScreen({navigation, tasks, setTasks}) {
  cacheFonts([FontAwesome.font]);
  let [input, setInput] = useState('');

  let updateTask = async (task) => {
    task.completed = !task.completed;
    setTasks([...tasks]);
    await AsyncStorage.setItem('@tasks', JSON.stringify(tasks));
  }
  let addTask = async () => {
    let maxKey = 0;
    tasks.forEach(task => {
      if(task.key > maxKey) {
        maxKey = task.key;
      }
    });
    let newTasks = [
      ...tasks, 
      {
        description: input,
        completed: false,
        key: maxKey + 1
      }
    ]
    setTasks(newTasks);
    console.log(tasks);
    await AsyncStorage.setItem('@tasks', JSON.stringify(newTasks));
    setInput('');
  }
  let renderItem = ({ item }) => {
    return <CheckBox
    textStyle={ item.completed ? {
      textDecorationLine: 'line-through', textDecorationStyle: 'solid'
    } : undefined}
    title={item.description}
    checked={item.completed}
    onPress={() => updateTask(item)} />
  }
}



export default async function App() {
  let [tasks, setTasks] = useState(startTasks);
  let useEffect;
  useEffect(() => {
    async function getValue() {
      const value = await AsyncStorage.getItem('@tasks');
      if (value === null) {
        console.log(
          'Storing list of serialized tasks' +
          JSON.stringify(tasks)
        );
      await AsyncStorage.setItem('@tasks', JSON.stringify(tasks));
      } else {
        let parsedVal = JSON.parse(value);
        console.log('Retrieving list of serialized tasks');
        console.log(parsedVal);
          setTasks(JSON.parse(value));
      }
    }
    getValue();
  }, []);

  return (
    <NavigationContainer>
      <View style={styles.container}>
      <Stack.Navigator initialRouteName='Todo List'>
        <Stack.Screen name='Todo List'>
          {(props) => (
            <TodoScreen {...props} tasks={tasks} setTasks={setTasks} />
          )}
        </Stack.Screen>

        <Stack.Screen name='Login'>
          {(props) => 
          <LoginScreen {...props}/>
          }

        </Stack.Screen>

        <Stack.Screen name='Registration'>

</Stack.Screen>
      </Stack.Navigator>

    <SafeAreaView style={[styles.container]}>
      <StatusBar style='auto' />
      <FlatList data={tasks} renderItem={renderItem} />
      <Input 
      onChangeText={setInput}
      value={input}
      placeholder='New task...'
      ></Input><Button title='Add' onPress={addTask}/>
    </SafeAreaView>
    </View>
    </NavigationContainer>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
