import { StyleSheet, View, Button, FlatList, Text, StatusBar, TouchableOpacity } from 'react-native'
import { useCallback, useEffect, useState } from "react"
import * as Font from 'expo-font'
import FontAwesome from '@expo/vector-icons/FontAwesome'

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}


let currentTimer = 0

export default function StopWatch() {
  cacheFonts([FontAwesome.font])  
  
  let [running, setRunning] = useState(false)
    let [timer, setTimer] = useState(0)
    let updateTimer = useCallback(() => {
      if(running) {
        setTimer((timer) => timer+10)
      }
    }, [running])
    useEffect(() => {
      currentTimer = setInterval(updateTimer, 10)
      return () => clearInterval(currentTimer)
    }, [running])
    let startStop = useCallback(() => {
      setRunning(!running)
      clearInterval(currentTimer)
    }, [running])
    let reset = useCallback(() => {
      setTimer(0)
    })

    let mins = (Math.floor((timer / (1000*60)) % 60)).toString().padStart(2, "0")
    let secs = (Math.floor((timer / 1000) % 60)).toString().padStart(2, "0")
    let mills = (timer % 1000).toString().padStart(3, "0")
    return (
    <View style={{width:"100vw", textAlign:"center"}}>
      <Text style={{fontSize:"7em", margin:"auto", fontFamily:"monospace"}}>{mins}:{secs}.{mills}</Text>
      <Button title={running ? "Pause" : "Start"} style={{"fontSize": "4em"}} onPress={startStop}/>
      <Button title='Reset' style={{fontSize:"4em"}} onPress={() => {
        setTimer(0)
      }} ></Button>
    </View>
    )
  }
