import { StyleSheet, View, FlatList, Text, StatusBar, TouchableOpacity } from 'react-native'
import { useState } from 'react'
import * as Font from 'expo-font'
import FontAwesome from '@expo/vector-icons/FontAwesome'
import * as React from 'react'
import { NavigationContainer } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { Button } from '@rneui/themed'
import StopWatch from './components/StopWatch'


async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  exercisesTitle: {
    fontWeight: 'bold',
    fontSize: 30,
    paddingBottom: 15,
  },
  buttonContainer: {
    marginVertical: 10,
    width: 200,
  },
  button: {
    padding: 10,
    backgroundColor: '#55D6BE',
    alignItems: 'center',
    borderRadius: 16,
  },
  buttonText: {
    color: '#fff',
    fontWeight: '600',
  },
})


const Stack = createNativeStackNavigator()

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen name='Home' component={Home} />
        <Stack.Screen name='RepetitionExercise' component={RepetitionExercise} />
        <Stack.Screen name='DurationExercise' component={DurationExercise} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}


function Home({ navigation }) {
  cacheFonts([FontAwesome.font])

  const exercises = [
    { name: 'Push Ups', type: 'repetition', suggested: 'Planks' },
    { name: 'Running', type: 'duration', suggested: 'Swimming' },
    { name: 'Planks', type: 'repetition', suggested: 'Push Ups' },
    { name: 'Swimming', type: 'duration', suggested: 'Running' },
  ]

  const ExerciseButton = ({ name, onPress }) => (
    <View style={styles.buttonContainer}>
      <TouchableOpacity
        title={name}
        onPress={onPress}
        style={styles.button}>
        <Text style={styles.buttonText}>{name}</Text>
      </TouchableOpacity>
    </View>
  )

  let renderItem = ({ item }) => (
    <ExerciseButton
      name={item.name}
      onPress={() =>
        navigation.navigate(
          item.type === 'repetition' ? 'RepetitionExercise' : 'DurationExercise',
          { name: item.name, suggested: item.suggested }
        )
      }
      suggested={item.suggested}
    />
  )
  return (
    <View style={styles.container}>
      <Text style={styles.exercisesTitle}>Exercises</Text>
      <FlatList
        data={exercises}
        renderItem={renderItem}
      />
    </View>
  )
}


function RepetitionExercise({ navigation }) {
  cacheFonts([FontAwesome.font])
  let [count, setCount] = useState(0)
  return (
    <View style={[styles.container]}>
      {/* <Text>{exercise.name}</Text> */}
      <Text>Rep Count: {count}</Text>
      <TouchableOpacity
        onPress={() => setCount(count => count + 1)}
        style={styles.button}>
          <Text style={styles.buttonText}>Add</Text>
        </TouchableOpacity>
      <TouchableOpacity
        onPress={() => setCount(0)}
        style={styles.button}>
          <Text style={styles.buttonText}>Reset</Text>
        </TouchableOpacity>
      <TouchableOpacity
      onPress={() => navigation.navigate('Home')}
      style={styles.button}>
        <Text style={styles.buttonText}>Home</Text>
      </TouchableOpacity>
    </View>
  )
}


function DurationExercise({ navigation }) {
  cacheFonts([FontAwesome.font])
  return (
    <View style={[styles.container]}>
      <Text></Text>
      <StopWatch />
      {/* <Button
      onPress={}></Button> */}
    </View>
  )
}
