import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text,  SafeAreaView, Button, FlatList, TextInput } from 'react-native';
import { CheckBox } from '@rneui/base';
import { useState } from 'react';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import React from 'react';

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

let tasks = [
  {key: '1', description: 'Task 1', completed: true},
  {key: '2', description: 'Task 2', completed: true}
]

export default function App() {
  cacheFonts([FontAwesome.font])
  let [checked, setChecked] = useState(true);
  let renderItem = ({ item }) => {
    <CheckBox title={item.description} checked={item.completed} onPress={() => setChecked(!checked)} />
  }

  return (
    <SafeAreaView style={styles.container}>
      <TextInput onChangeText={(text) => {setTextContent(text)}}></TextInput>
      <StatusBar style='auto' />
      <Text>Tasks</Text>
      <FlatList data={tasks} renderItem={renderItem} style={{textDecorationLine: 'line-through', textDecorationStyle: 'solid'}}></FlatList>
    </SafeAreaView>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingTop: Platform.OS === 'android' ? 25 : 0
  },
});
