import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, Button, FlatList, Text, View } from 'react-native';
import { CheckBox, Input } from '@rneui/themed';
import { useCallback, useEffect, useState } from 'react';
import * as Font from 'expo-font';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import AsyncStorage from '@react-native-async-storage/async-storage';

async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font));
}

function LoginScreen({ navigation, route }) {
  cacheFonts([FontAwesome.font]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // used Chat GPT to help with AsyncStorage/handleLogin
  const handleLogin = async () => {
    try {
      const loginData = await AsyncStorage.getItem('loginData');
      if (loginData) {
        const users = JSON.parse(loginData);
        const user = users.find(u => u.username === username && u.password === password);
        const userTest = users.find(() => username === 'test' && password === 'Test1@');
        if (user || userTest) {
          console.log(loginData);
          navigation.navigate('Todo');
        } else {
          alert('Invalid username or password');
        }
      } else {
        alert('No users registered yet');
      }
    } catch (error) {
      console.error('Error retrieving login data: ', error);
    }
  };

  return (
    <SafeAreaView style={[styles.container]}>
      <Input
      placeholder='Username'
      testID='login-username'
      value={username}
      onChangeText={(value) => { setUsername(value) }}
      />
      <Input placeholder='Password'
      testID='login-password'
      value={password}
      secureTextEntry={true}
      onChangeText={(value) => { setPassword(value) }}
      />
      <Button title='Login'
      testID='login-button'
      onPress={handleLogin}
      />
      <Button title='Register'
      testID='login-register'
      onPress={() =>
      navigation.navigate('Registration')}
      />
    </SafeAreaView>
  )
}

