// used Chat GPT to help with RegistrationScreen
const RegistrationScreen = () => {
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    username: '',
    phonenumber: '',
    password: '',
    confirmpassword: '',
    email: '',
    zip: '',
    newsletter: false,
  });
  const [errors, setErrors] = useState({});
  const [registerDisabled, setRegisterDisabled] = useState(true);

  const handleInputChange = (name, value) => {
    setFormData({ ...formData, [name]: value });
    validateInput(name, value);
    handleInputValidity
  };

  const validateInput = (name, value) => {
    let errorMessage = '';

    switch (name) {
      case 'firstname':
      case 'lastname':
        errorMessage = /^[^\d=?\\/@#%^&*()]+$/.test(value)
          ? ''
          : 'Error: Must only include word or symbol characters, no numbers';
        break;
      case 'phonenumber':
        errorMessage = /^\(\d{3}\) \d{3}-\d{4}$/.test(value)
          ? ''
          : 'Error: Phone Number must be in the format (xxx) xxx-xxxx';
        break;
      case 'email':
        errorMessage = /[^@ \t\r\n]+@[^@ \t\r\n]+\.[^@ \t\r\n]+/.test(value)
          ? ''
          : 'Error: Must be a valid email address';
        break;
      case 'password':
        errorMessage = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/.test(value)
          ? ''
          : 'Error: Password must contain at least one uppercase letter, one lowercase letter, one number, one special character, and be at least 8 characters long';
        break;
      case 'confirmpassword':
        errorMessage = value === formData.password ? '' : 'Error: Passwords do not match';
        break;
      case 'zip':
        errorMessage = /^\d{5}$/.test(value) ? '' : 'Error: ZIP Code must be 5 digits';
        break;
      default:
        break;
    }

    setErrors({ ...errors, [name]: errorMessage });
  };

  const handleBlur = (name) => {
    validateInput(name, formData[name]);
  };

  const handleInputValidity = () => {
    const isValid = Object.values(errors).every((error) => !error) && Object.values(formData).every((value) => !!value);
    setRegisterDisabled(!isValid);
  };

  const handleRegister = async () => {
    // Check if all fields are valid before registering
    if (Object.values(errors).every((error) => !error)) {
      // Register user
      try {
        // Add the new user to AsyncStorage
        const loginDataString = await AsyncStorage.getItem('loginData');
        const loginData = loginDataString ? JSON.parse(loginDataString) : [];
        loginData.push({ username: formData.username, password: formData.password });
        await AsyncStorage.setItem('loginData', JSON.stringify(loginData));
        // Reset form after successful registration
        setFormData({
          firstname: '',
          lastname: '',
          username: '',
          phonenumber: '',
          password: '',
          confirmpassword: '',
          email: '',
          zip: '',
          newsletter: false,
        });
        setErrors({});
        setRegisterDisabled(true);
        alert('Registration Successful');
      } catch (error) {
        console.error('Error saving registration data:', error);
        alert('Registration Failed. Please try again.');
      }
    } else {
      alert('Please correct all errors before registering.');
    }
  };

  return (
    <View style={styles.container}>
      <Input
        testID="firstname"
        label="First Name"
        value={formData.firstname}
        onChangeText={(value) => handleInputChange('firstname', value)}
        onBlur={() => handleBlur('firstname')}
        errorMessage={errors.firstname}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="lastname"
        label="Last Name"
        value={formData.lastname}
        onChangeText={(value) => handleInputChange('lastname', value)}
        onBlur={() => handleBlur('lastname')}
        errorMessage={errors.lastname}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="username"
        label="Username"
        value={formData.username}
        onChangeText={(value) => handleInputChange('username', value)}
        onBlur={() => handleBlur('username')}
        errorMessage={errors.username}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="phonenumber"
        label="Phone Number"
        value={formData.phonenumber}
        onChangeText={(value) => handleInputChange('phonenumber', value)}
        onBlur={() => handleBlur('phonenumber')}
        errorMessage={errors.phonenumber}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="password"
        label="Password"
        secureTextEntry
        value={formData.password}
        onChangeText={(value) => handleInputChange('password', value)}
        onBlur={() => handleBlur('password')}
        errorMessage={errors.password}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="confirmpassword"
        label="Confirm Password"
        secureTextEntry
        value={formData.confirmpassword}
        onChangeText={(value) => handleInputChange('confirmpassword', value)}
        onBlur={() => handleBlur('confirmpassword')}
        errorMessage={errors.confirmpassword}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="email"
        label="Email"
        value={formData.email}
        onChangeText={(value) => handleInputChange('email', value)}
        onBlur={() => handleBlur('email')}
        errorMessage={errors.email}
        onEndEditing={handleInputValidity}
      />
      <Input
        testID="zip"
        label="ZIP Code"
        value={formData.zip}
        onChangeText={(value) => handleInputChange('zip', value)}
        onBlur={() => handleBlur('zip')}
        errorMessage={errors.zip}
        onEndEditing={handleInputValidity}
      />
      <Button
        title="Register"
        onPress={handleRegister}
        disabled={registerDisabled}
      />
    </View>
  );
};



