import { StatusBar } from 'expo-status-bar'
import { StyleSheet, SafeAreaView, Button, FlatList } from 'react-native'
import { useState } from 'react'
import * as Font from 'expo-font'
import FontAwesome from '@expo/vector-icons/FontAwesome'
import * as React from 'react'
import { NavigationContainer } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'


async function cacheFonts(fonts) {
  return fonts.map(async (font) => await Font.loadAsync(font))
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  button: {
    margin: 10,
  }
})


const Stack = createNativeStackNavigator()

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen name='Home' component={Home} />
        <Stack.Screen name='RepetitionExercise' component={RepetitionExercise} />
        <Stack.Screen name='DurationExercise' component={DurationExercise} />
      </Stack.Navigator>
    </NavigationContainer>
  )
}


function Home({ navigation }) {
  cacheFonts([FontAwesome.font])

  const exercises = [
    { name: 'Push Ups', type: 'repetition', suggested: 'Planks' },
    { name: 'Running', type: 'duration', suggested: 'Swimming' },
    { name: 'Planks', type: 'repetition', suggested: 'Push Ups' },
    { name: 'Swimming', type: 'duration', suggested: 'Running' },
  ]

  const ExerciseButton = ({ name, onPress }) => (
    <Button
      title={name}
      onPress={onPress}
    />
  )

  let renderItem = ({ item }) => (
    <ExerciseButton
      name={item.name}
      onPress={() =>
      navigation.navigate(
        item.type === 'repetition' ? 'RepetitionExercise' : 'DurationExercise',
        { name: item.name, suggested: item.suggested }
      )
    }
    suggested={item.suggested}
    />
    )
    return (
      <SafeAreaView style={styles.container}>
        <FlatList
        data={exercises}
        renderItem={renderItem}
        />
      </SafeAreaView>
    )
  }


function RepetitionExercise({ navigation }) {
  cacheFonts([FontAwesome.font])

  return (
    <SafeAreaView style={[styles.container]}>

    </SafeAreaView>
  )
}


function DurationExercise({ navigation }) {
  cacheFonts([FontAwesome.font])

  return (
    <SafeAreaView style={[styles.container]}>

    </SafeAreaView>
  )
}
